// 导出一个工厂函数，用于创建新的
// 应用程序、router 和 store 实例
export function createApp () {
  // 创建 router 和 store 实例
  const router = createRouter()
  const store = createStore()

  const app = new Vue({
    // 注入 router 和 store 到根 Vue 实例
    router,
    store,
    render: h => h(App)
  })

  // 返回 app 和 router 和 store
  return { app, router, store }
}

// entry-server.js
import { createApp } from './app.js'

export default context => {
  // 因为有可能会是异步路由钩子函数或组件，所以我们将返回一个 Promise，
  // 以便服务器能够等待所有的内容在渲染前，
  // 就已经准备就绪。
  return new Promise((resolve, reject) => {
    const { app, router, store } = createApp()
    // 设置服务器端 router 的位置
    router.push(context.url)

    // 等到 router 将可能的异步组件和钩子函数解析完
    router.onReady(() => {
      const matchedComponents = router.getMatchedComponents()
      // 匹配不到的路由，执行 reject 函数，并返回 404
      if (!matchedComponents.length) {
        return reject({ code: 404 })
      }

      // 对所有匹配的路由组件调用 `asyncData()`
      Promise.all(matchedComponents.map(Component => {
        if (Component.asyncData) {
          console.log('server-asyncData')
          return Component.asyncData({
            store,
            route: router.currentRoute
          })
        }
      })).then(() => {
        // 在所有预取钩子(preFetch hook) resolve 后，
        // 我们的 store 现在已经填充入渲染应用程序所需的状态。
        // 当我们将状态附加到上下文，
        // 并且 `template` 选项用于 renderer 时，
        // 状态将自动序列化为 `window.__INITIAL_STATE__`，并注入 HTML。
        context.state = store.state

        // Promise 应该 resolve 应用程序实例，以便它可以渲染
        resolve(app)
      }).catch(reject)

    }, reject)
  })
}

import Vue from 'vue';
import Router from 'vue-router';
Vue.use(Router);

export function createRouter() {
  const routes = [
    {
      path: '/',
      redirect: '/list'
    },
    {
      path: '/detail',
      name: 'detail',
      component: () => import('../Page/Detail.vue'), // 异步路由
      children: [
        {
          path: '/detail1',
          name: 'detailFir',
          component: () => import('../Page/DetailFir.vue') // 异步路由
        },
        {
          path: '/detail2',
          name: 'detailSec',
          component: () => import('../Page/DetailSec.vue') // 异步路由
        }
      ]
    },
    {
      path: '/list',
      name: 'list',
      component: () => import('../Page/List.vue') // 异步路由
    }
  ];

  const router = new Router({
    mode: 'history',
    routes
  });

  return router;
}

import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

// 模拟一个返回promise的异步api
const fetchDetail = function (id) {
    return new Promise((reslove, reject) => {
      const detail = {
        id,
        name: `name${Math.random()}`
      }
      setTimeout(() => {
        reslove(detail)
      }, 1000)
    })
}

export function createStore () {
  return new Vuex.Store({
    state: {
      detail: {}
    },
    actions: {
      fetchDetail ({ commit }, id) {
        // `store.dispatch()` 会返回 Promise，
        // 以便我们能够知道数据在何时更新
        return fetchDetail(id).then(detail => {
          commit('setDetail', detail)
        })
      }
    },
    mutations: {
      setDetail (state, detail) {
        state.detail = detail
      }
    }
  })
}
